pragma solidity ^0.5.0;
import "openzeppelin-solidity/contracts/token/ERC20/ERC20.sol";
import "openzeppelin-solidity/contracts/token/ERC20/ERC20Detailed.sol";

contract HelloWorld is ERC20, ERC20Detailed {

  string private _name = "HelloWorld";
  string private _symbol = "HW";
  uint8 private _decimals = 18;

  address account = msg.sender;
  uint value = 100000000000000000000;

  constructor() ERC20Detailed( _name, _symbol, _decimals) public {
    _mint(account, value);
  }
}

contract TestCoin is ERC20, ERC20Detailed {
  string private _name = "TestCoin";
  string private _symbol = "TC";
  uint8 private _decimals = 4;

  address account = msg.sender;
  uint value = 300000000000000000000;

  constructor() ERC20Detailed( _name, _symbol, _decimals) public {
    _mint(account, value);
  }
}
